<?phpnamespace App\Http\Controllers;use App\Config;use App\Http\Requests\ConfigEmailRequest;use Illuminate\Http\Request;class ConfigController extends Controller{    public function getConfig()    {        $cauhinhs = Config::whereIn('name', ['config-contact'])->orderBy('order')->get();        return view('backend.admin.config.index', compact('cauhinhs'));    }    public function saveConfig(Request $request)    {        $configContact = $request->input('config-contact');        $hdconfigContact = $request->input('hd-config-contact');        if (strcmp(trim($configContact), trim($hdconfigContact)) != 0) {            $config = Config::where('name', 'config-contact')->first();            $config->content = $configContact;            $config->save();        }        return redirect()->route('config.index')            ->with('success', 'Cấu Hình Lưu Thành Công');    }}